import React, { useEffect, useState, useCallback } from 'react';
import type { ColumnDef } from '@tanstack/react-table';
import DashboardLayout from '../../dashboard/layout';
import axios from 'axios';
import raportService from '../../services/raportService';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { useToast } from '../../components/ui/toast';
import tahunAjaranService from '../../services/tahunAjaranService';
import { Label } from '../../components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../../components/ui/select';
import { 
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSub,
  DropdownMenuSubTrigger,
  DropdownMenuSubContent,
  DropdownMenuSeparator,
  DropdownMenuPortal,
} from '../../components/ui/dropdown-menu';
import { ChevronDown } from 'lucide-react';
import { getAllTingkatans } from '../../services/tingkatanService';
import DataTable from '../../components/data-table';
const API_BASE = 'http://localhost:5000/api';

// Tipe data
type SiswaRow = {
  id: number;
  nis: string;
  nama: string;
  currentHistory?: {
    id: number;
    kelas_id: number;
    catatan_akademik?: string;
    catatan_sikap?: string;
  };
  kelas?: {
      nama_kelas: string;
  }
};

type BatchRow = { id: number; nama: string; keterangan?: string };

type MasterTahunAjaran = {
  id: number;
  nama_ajaran: string;
};

type Periode = {
  id: number;
  semester: number;
  nama_periode?: string;
};

type Tingkatan = {
  id: number;
  nama_tingkatan: string;
};

type Kelas = {
  id: number;
  nama_kelas: string;
  tingkatan_id: number;
};

export default function ManajemenRaportDashboardPage() {
  // State
  const [batchData, setBatchData] = useState<BatchRow[]>([]);
  const [loadingBatch, setLoadingBatch] = useState(true);
  const [siswaList, setSiswaList] = useState<SiswaRow[]>([]);
  const [loadingSiswa, setLoadingSiswa] = useState(false); // Dimulai dari false
  const [masterOptions, setMasterOptions] = useState<MasterTahunAjaran[]>([]);
  const [periodeOptions, setPeriodeOptions] = useState<Periode[]>([]);
  const [tingkatanOptions, setTingkatanOptions] = useState<Tingkatan[]>([]);
  const [kelasOptions, setKelasOptions] = useState<Kelas[]>([]);
  const [selectedMaster, setSelectedMaster] = useState<number | string>('_all');
  const [selectedPeriode, setSelectedPeriode] = useState<number | string>('_all');
  const [selectedTingkatan, setSelectedTingkatan] = useState<number | string>('_all');
  const [selectedKelas, setSelectedKelas] = useState<number | string>('_all');

  const { toast } = useToast();

  const handleGenerate = async (type: 'identitas' | 'nilai' | 'sikap', siswaId: number) => {
    if ((type === 'nilai' || type === 'sikap') && typeof selectedPeriode !== 'number') {
      return toast({ title: 'Pilih Periode', description: 'Pilih tahun ajaran dan semester dulu', variant: 'destructive' });
    }
    try {
      let resp;
      const semester = periodeOptions.find(p => p.id === selectedPeriode)?.semester;
      if (type === 'identitas') resp = await raportService.generateIdentitas(siswaId);
      // Pastikan selectedPeriode adalah angka sebelum memanggil API
      if (type === 'nilai' && typeof selectedPeriode === 'number') resp = await raportService.generateNilai(siswaId, selectedPeriode, semester);
      if (type === 'sikap' && typeof selectedPeriode === 'number') resp = await raportService.generateSikap(siswaId, selectedPeriode, semester);
      
      if (resp) {
        downloadBlob(resp, `Laporan_${type}_${siswaId}.docx`);
        toast({ title: 'Sukses', description: 'File laporan berhasil diunduh.' });
      }
    } catch (e: any) {
      console.error(e);
      toast({ title: 'Gagal Generate', description: e?.response?.data?.message || 'Gagal membuat file laporan.', variant: 'destructive' });
    }
  };

  const downloadBlob = (resp: any, defaultName: string) => {
    const blob = new Blob([resp.data], { type: resp.headers['content-type'] });
    const link = document.createElement('a');
    link.href = window.URL.createObjectURL(blob);
    let fileName = defaultName;
    const cd = resp.headers['content-disposition'];
    if (cd) {
      const m = cd.match(/filename="(.+)"/);
      if (m) fileName = m[1];
    }
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    link.remove();
    window.URL.revokeObjectURL(link.href);
  };

  const columns: ColumnDef<SiswaRow, any>[] = [
    { header: 'NIS', accessorKey: 'nis' },
    { header: 'Nama', accessorKey: 'nama' },
    { header: 'Kelas', accessorKey: 'kelas.nama_kelas' },
    {
      id: 'actions',
      header: 'Aksi',
      cell: ({ row }) => {
        const [open, setOpen] = React.useState(false);
        return (
          <DropdownMenu open={open} onOpenChange={setOpen}>
            <DropdownMenuTrigger asChild>
              <Button variant="outline" size="sm">
                <span className="sr-only">Open menu</span>
                <ChevronDown className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuSub>
                <DropdownMenuSubTrigger>Generate</DropdownMenuSubTrigger>
                <DropdownMenuPortal>
                  <DropdownMenuSubContent>
                    <DropdownMenuItem onClick={() => handleGenerate('identitas', row.original.id)}>
                      Generate Identitas
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => handleGenerate('nilai', row.original.id)}>
                      Generate Nilai
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => handleGenerate('sikap', row.original.id)}>
                      Generate Sikap
                    </DropdownMenuItem>
                  </DropdownMenuSubContent>
                </DropdownMenuPortal>
              </DropdownMenuSub>
              <DropdownMenuSeparator />
              <DropdownMenuItem>
                Lihat Data
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        );
      }
    }
  ];

  const fetchBatchData = useCallback(async () => {
    setLoadingBatch(true);
    try {
      const res = await raportService.getRaportList();
      setBatchData(res);
    } catch (e) {
      console.error(e);
    } finally {
      setLoadingBatch(false);
    }
  }, []);

  const loadSiswa = useCallback(async () => {
    // Validasi bahwa filter sudah dipilih
    if (selectedPeriode === '_all' || selectedKelas === '_all') {
       toast({ title: 'Informasi', description: 'Silakan pilih Tahun Ajaran/Semester dan Kelas spesifik.' });
       setSiswaList([]);
       return;
    }

    setLoadingSiswa(true);
    try {
      const params: any = {};
      if (selectedPeriode !== '_all') params.tahun_ajaran_id = selectedPeriode;
      if (selectedKelas !== '_all') params.kelas_id = selectedKelas;
      if (selectedTingkatan !== '_all') params.tingkatan_id = selectedTingkatan;
      if (selectedMaster !== '_all' && !params.tahun_ajaran_id) {
          // Jika master dipilih tapi periode tidak, ini bisa jadi masalah.
          // Untuk sementara, kita asumsikan get siswa bisa handle master_ta_id
          params.master_ta_id = selectedMaster;
      }
      
      const res = await axios.get<SiswaRow[]>(`${API_BASE}/siswa`, { params });
      setSiswaList(res.data || []);
      if ((res.data || []).length === 0) {
        toast({ title: 'Informasi', description: 'Tidak ada siswa yang ditemukan.' });
      }
    } catch (e) {
      console.error(e);
      toast({ title: 'Gagal Memuat Siswa', description: 'Gagal mengambil data siswa.', variant: 'destructive' });
      setSiswaList([]);
    } finally {
      setLoadingSiswa(false);
    }
  }, [selectedPeriode, selectedKelas, selectedTingkatan, selectedMaster, toast]);

  useEffect(() => {
    const initialize = async () => {
      try {
        const masters = await tahunAjaranService.getAllMasterTahunAjaran();
        const tingkatans = await getAllTingkatans();
        setMasterOptions(masters);
        setTingkatanOptions(tingkatans);
      } catch (error) {
        console.error("Initialization failed:", error);
        toast({ title: 'Inisialisasi Gagal', description: 'Gagal memuat data filter awal.', variant: 'destructive' });
      }
    };
    
    initialize();
    fetchBatchData();
  }, [fetchBatchData, toast]);

  useEffect(() => {
    if (selectedMaster === '_all') {
      setPeriodeOptions([]);
      setSelectedPeriode('_all');
      return;
    }
    
    const fetchPeriodes = async () => {
      try {
        const periodes = await tahunAjaranService.getPeriodesForMaster(selectedMaster);
        setPeriodeOptions(periodes);
        setSelectedPeriode('_all');
      } catch (error) {
        console.error('Failed to fetch periodes:', error);
        setPeriodeOptions([]);
        setSelectedPeriode('_all');
      }
    };
    
    fetchPeriodes();
  }, [selectedMaster]);

  useEffect(() => {
    if (selectedTingkatan === '_all') {
      setKelasOptions([]);
      setSelectedKelas('_all');
      return;
    }
    
    const fetchKelas = async () => {
      try {
        const resp = await axios.get(`${API_BASE}/kelas`, { params: { tingkatan_id: selectedTingkatan } });
        setKelasOptions(resp.data || []);
        setSelectedKelas('_all');
      } catch (err) {
        console.error('Failed fetching kelas for tingkatan', err);
        setKelasOptions([]);
        setSelectedKelas('_all');
      }
    };
    
    fetchKelas();
  }, [selectedTingkatan]);


  return (
    <DashboardLayout>
      <div className="space-y-6">
        <div>
          <h1 className="text-3xl font-bold">Manajemen Raport</h1>
          <p className="text-muted-foreground">Pilih filter untuk menampilkan siswa, lalu kelola raport dan catatan per individu.</p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Filter Siswa</CardTitle>
            <CardDescription>Ubah pilihan di bawah ini untuk memuat daftar siswa.</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="space-y-2">
                <Label htmlFor="master-ajaran">Tahun Ajaran</Label>
                <Select
                  value={String(selectedMaster)}
                  onValueChange={(value) => setSelectedMaster(value === '_all' ? '_all' : Number(value))}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Pilih Tahun Ajaran" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="_all">Tampilkan Semua</SelectItem>
                    {masterOptions.map(m => (
                      <SelectItem key={m.id} value={String(m.id)}>
                        {m.nama_ajaran}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="periode">Semester / Periode</Label>
                <Select
                  value={String(selectedPeriode)}
                  onValueChange={(value) => setSelectedPeriode(value === '_all' ? '_all' : Number(value))}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Pilih Semester" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="_all">Tampilkan Semua</SelectItem>
                    {periodeOptions.map(p => (
                      <SelectItem key={p.id} value={String(p.id)}>
                        {`Semester ${p.semester}`}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="tingkatan">Tingkatan</Label>
                <Select
                  value={String(selectedTingkatan)}
                  onValueChange={(value) => setSelectedTingkatan(value === '_all' ? '_all' : Number(value))}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Pilih Tingkatan" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="_all">Tampilkan Semua</SelectItem>
                    {tingkatanOptions.map(t => (
                      <SelectItem key={t.id} value={String(t.id)}>
                        {t.nama_tingkatan}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="kelas">Kelas</Label>
                <Select
                  value={String(selectedKelas)}
                  onValueChange={(value) => setSelectedKelas(value === '_all' ? '_all' : Number(value))}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Pilih Kelas" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="_all">Tampilkan Semua</SelectItem>
                    {kelasOptions.map(k => (
                      <SelectItem key={k.id} value={String(k.id)}>
                        {k.nama_kelas}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Daftar Siswa</CardTitle>
            <CardDescription>Ditemukan {siswaList.length} siswa. Generate laporan atau perbarui catatan di bawah.</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex justify-end mb-4">
              <Button onClick={loadSiswa} disabled={loadingSiswa}>
                {loadingSiswa ? "Memuat..." : "Muat Siswa"}
              </Button>
            </div>
            
            {loadingSiswa ? (
              <div className="text-center text-muted-foreground">Memuat data siswa...</div>
            ) : siswaList.length === 0 ? (
              <div className="text-center text-muted-foreground">Belum ada siswa yang dimuat. Klik "Muat Siswa" untuk menampilkan data.</div>
            ) : (
              <DataTable
                columns={columns}
                data={siswaList}
              />
            )}
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
}
