import React from 'react'
import { useReactTable, getCoreRowModel, flexRender, ColumnDef } from '@tanstack/react-table'
import { Table, TableHeader, TableBody, TableRow, TableHead, TableCell } from './ui/table'
import { DropdownMenu, DropdownMenuItem } from './ui/dropdown'
import { DropdownMenuTrigger, DropdownMenuContent } from '@radix-ui/react-dropdown-menu'
import { Button } from './ui/button'
import { MoreHorizontal } from 'lucide-react'


type DataTableProps<T> = {
  columns: ColumnDef<T, any>[]
  data: T[]
  onEdit?: (row: T) => void
  onDelete?: (row: T) => void
  onPrint?: (row: T, reportType: 'nilai' | 'sikap' | 'identitas', format: 'docx' | 'pdf') => void
  searchKey?: string
}

export default function DataTable<T>({ columns, data, onEdit, onDelete, onPrint, searchKey }: DataTableProps<T>) {
  const table = useReactTable({ data, columns, getCoreRowModel: getCoreRowModel() })

  return (
    <div className="space-y-4">
      <div className="rounded-md border bg-white">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map(hg => (
              <tr key={hg.id}>
                {hg.headers.map(h => (
                  <TableHead key={h.id}>{h.isPlaceholder ? null : flexRender(h.column.columnDef.header, h.getContext())}</TableHead>
                ))}
              </tr>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows.length ? table.getRowModel().rows.map(row => (
              <TableRow key={row.id}>
                {row.getVisibleCells().map(cell => (
                  <TableCell key={cell.id}>
                    {cell.column.id === 'actions' ? (
                      <div className="flex justify-end">
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" className="h-8 w-8 p-0">
                              <span className="sr-only">Open menu</span>
                              <MoreHorizontal className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            {/* Opsi Print: hanya render jika ada callback onPrint */}
                            {onPrint && (
                              <>
                                <DropdownMenuItem onClick={() => onPrint?.(row.original as T, 'nilai', 'docx')}>Cetak Rapor Nilai</DropdownMenuItem>
                                <DropdownMenuItem onClick={() => onPrint?.(row.original as T, 'sikap', 'docx')}>Cetak Rapor Sikap</DropdownMenuItem>
                                <DropdownMenuItem onClick={() => onPrint?.(row.original as T, 'identitas', 'docx')}>Cetak Identitas</DropdownMenuItem>
                              </>
                            )}

                            {/* Opsi Edit/Hapus: hanya render jika ada callback */}
                            {onEdit && <DropdownMenuItem onClick={() => onEdit?.(row.original as T)}>Edit</DropdownMenuItem>}
                            {onDelete && <DropdownMenuItem onClick={() => onDelete?.(row.original as T)}>Hapus</DropdownMenuItem>}
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>
                    ) : (
                      flexRender(cell.column.columnDef.cell, cell.getContext())
                    )}
                  </TableCell>
                ))}
              </TableRow>
            )) : (
              <TableRow>
                <TableCell colSpan={columns.length} className="h-24 text-center">
                  Tidak ada hasil.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  )
}

// ensure this file is treated as a module under --isolatedModules
export {}
